#Define some colors to use throughout my_colors <- c("#E69F00", "#56B4E9", "#009E73", "#CC79A7", "#D55E00", "#D65E00")
#Customize ggplot2's default theme settings #This tutorial doesn't actually pass any parameters, but you may use it again in future tutorials so it's nice to have the options theme_lyrics <- function(aticks = element_blank(), pgminor = element_blank(), lt = element_blank(), lp = "none") { theme(plot.title = element_text(hjust = 0.5), #Center the title axis.ticks = aticks, #Set axis ticks to on or off panel.grid.minor = pgminor, #Turn the minor grid lines on or off legend.title = lt, #Turn the legend title on or off legend.position = lp) #Turn the legend on or off }
#Customize the text tables for consistency using HTML formatting my_kable_styling <- function(dat, caption) { kable(dat, "html", escape = FALSE, caption = caption) %>% kable_styling(bootstrap_options = c("striped", "condensed", "bordered"), full_width = FALSE) }

